import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';


function App(){

    const [livros, setlivros] = useState([
      { tipo: 'olha', titulo: 'oba', autor: 'teste', edicao: '545', issn: '54545', ano: '545', editora: '545'}
    ])
    const [novotitulo, setnovotitulo] = useState('46546545');
    const [novoautor, setnovoautor] = useState('shekton');


    function adiciona(event){

      event.preventDefault();
      let novoTitulo = novotitulo;
      let novoAutor = novoautor;
      let c = {autor: novoAutor, titulo: novoTitulo};

      setlivros([...livros, c]);
      novotitulo = '';
      novoautor = '';
      console.log(livros);

     //console.log(novoAutor, novoTitulo);

    }


    return(
      <div>
        <h1 className="text-center">Sistema para Solicitação de Livros - SiSoLi</h1>
        <div className=" text-center time" id="timer"></div>
        <div className = "container">
          <form onSubmit={adiciona} id="form" method="post">
            <div className="row g-3">
              <div  className="col-md-6">
                <label className="form-label">Tipo de vínculo</label>
                <select id="tipo" className="form-select" required>
                    <option value="Docente">Docente</option>
                    <option value="Discente">Discente</option>
                    <option value="Técnico">Técnico</option>
                </select>
              </div>
              <div className="col-md-6">
                <label className="form-label">Titulo</label>
                <input id="titulo" type="text" value={novotitulo} onChange = { (event) => setnovotitulo(event.target.value)} className="form-control" required/>
              </div>
              <div className="col-md-6">
                    <label className="form-label">Autor</label>
                    <input id="autor" type="text" value={novoautor} onChange = { (event) => setnovoautor(event.target.value)} className="form-control" required />
                </div>
                <div className="col-md-6">
                    <label className="form-label">Edição</label>
                    <input id="edicao" type="number" className="form-control" />
                </div>
                <div className="col-md-4">
                    <label className="form-label">ISSN</label>
                    <input id="issn" type="number" className="form-control" />
                </div>
                <div className="col-md-4">
                    <label className="form-label">Ano Publicação</label>
                    <input id="ano" type="number" className="form-control"  />
                </div>
                <div className="col-md-4">
                    <label className="form-label">Editora</label>
                    <input id="editora" type="text" className="form-control" />
                </div>
                <div className="col-md">
                    <button id="btn" className="btn btn-primary" >Solicitar Livro</button>
                </div>
            </div>

          </form >

          <h2 className="text-center tabela">Tabela de Soliciração</h2>
          <table id="table" className="table table-hover">
              <thead>
                <tr>
                  <th>Vinculo</th>
                  <th>Título</th>
                  <th>Autor</th>
                  <th>Edição</th>
                  <th>ISSN</th>
                  <th>Ano</th>
                  <th>Editora</th>

                </tr>

              </thead>
              <tbody id="corpo">

                {livros.map( item => {
                  return(
                    <tr>
                      <td>{item.tipo}</td>
                      <td>{item.titulo}</td>
                      <td>{item.autor}</td>
                      <td>{item.edicao}</td>
                      <td>{item.issn}</td>
                      <td>{item.ano}</td>
                      <td>{item.editora}</td>
                    </tr>
                  )
                })}

              </tbody>
          </table>
        </div>
      </div>
    );

}

export default App;
